			componentDidMount(){
		var modal = document.getElementsByClassName("myImgFull");

		// Get the button that opens the modal
		var btn = document.getElementsByClassName("myImg");

		// Get the <span> element that closes the modal
		var span = document.getElementsByClassName("closeImg");

		// When the user clicks the button, open the modal 
		btn[0].onclick = function() {
		  modal[0].style.display = "block";
		}

		btn[1].onclick = function() {
		  modal[1].style.display = "block";
		}

		btn[2].onclick = function() {
		  modal[2].style.display = "block";
		}

		btn[3].onclick = function() {
		  modal[3].style.display = "block";
		}

		btn[4].onclick = function() {
		  modal[4].style.display = "block";
		}

		// When the user clicks on <span> (x), close the modal
		span[0].onclick = function() {
		  modal[0].style.display = "none";
		}

		span[1].onclick = function() {
		  modal[1].style.display = "none";
		}

		span[2].onclick = function() {
		  modal[2].style.display = "none";
		}

		span[3].onclick = function() {
		  modal[3].style.display = "none";
		}

		span[4].onclick = function() {
		  modal[4].style.display = "none";
		}


		// When the user clicks anywhere outside of the modal, close it
		window.onclick = function(event) {
		  if (event.target == modal[0]) {
		    modal[0].style.display = "none";
		  }

		  if (event.target == modal[1]) {
		    modal[1].style.display = "none";
		  }

		  if (event.target == modal[2]) {
		    modal[2].style.display = "none";
		  }

		  if (event.target == modal[3]) {
		    modal[3].style.display = "none";
		  }

		  if (event.target == modal[4]) {
		    modal[4].style.display = "none";
		  }
		}
	}
	render() {
		return (
			<div id = "Art">
				<h1 
				data-aos="slide-right"
				data-aos-delay="200"
				data-aos-duration="700"
				data-aos-once="true"
				className = "artHeader"><my className = "my">MY</my> ART</h1>
				<div className = "artLineOdd">
				<div data-aos="fade-up"
				data-aos-delay="900"
				data-aos-duration="700"
				data-aos-once="true"
				data-aos-anchor = "#Art"
				className = "artImg">

					<img className =  "myImg artImg" src = {turtle}/>
					<div className="myImgFull modal">
						  <div className="modal-content">
						    <span className="closeImg close">&times;</span>
							<h1 className = "artTitle">Turtle</h1>
							<img className = "imgFull" src = {turtle}/>
						  </div>
					</div>

					</div>
					<div data-aos="fade-up"
				data-aos-delay="900"
				data-aos-duration="700"
				data-aos-once="true"
				data-aos-anchor = "#Art"
				className = "artImg">

					<img className =  "myImg artImg" src = {whale}/>
					<div className="myImgFull modal">
						  <div className="modal-content">
						    <span className="closeImg close">&times;</span>
							<h1 className = "artTitle">Whale</h1>
							<img className = "imgFull" src = {whale}/>
						  </div>
					</div>

					</div>

					<div data-aos="fade-up"
				data-aos-delay="900"
				data-aos-duration="700"
				data-aos-once="true"
				data-aos-anchor = "#Art"
				className = "artImg">

					<img className =  "myImg artImg" src = {jam}/>
					<div className="myImgFull modal">
						  <div className="modal-content">
						    <span className="closeImg close">&times;</span>
							<h1 className = "artTitle">Jam</h1>
							<img className = "imgFull" src = {jam}/>
						  </div>
					</div>
					</div>
				</div>

				<div className = "artLineEven">
				<div data-aos="fade-up"
				data-aos-delay="1500"
				data-aos-duration="700"
				data-aos-once="true"
				data-aos-anchor = "#Art"
				className = "artImg">
						<div data-aos="fade-up"
				data-aos-delay="900"
				data-aos-duration="700"
				data-aos-once="true"
				data-aos-anchor = "#Art"
				className = "artImg">

					<img className =  "myImg artImg" src = {elephant}/>
					<div className="myImgFull modal">
						  <div className="modal-content">
						    <span className="closeImg close">&times;</span>
							<h1 className = "artTitle">Elephant</h1>
							<img className = "imgFull" src = {elephant}/>
						  </div>
					</div>

					</div>

					<div data-aos="fade-up"
				data-aos-delay="900"
				data-aos-duration="700"
				data-aos-once="true"
				data-aos-anchor = "#Art"
				className = "artImg">

					<img className =  "myImg artImg" src = {turtle}/>
					<div className="myImgFull modal">
						  <div className="modal-content">
						    <span className="closeImg close">&times;</span>
							<h1 className = "artTitle">Turtle</h1>
							<img className = "imgFull" src = {turtle}/>
						  </div>
					</div>

					</div>

					</div>
				</div>
				<hr className = "hr"></hr>
			</div>
		);


	}
}

export default Art;
