import React, {Component} from "react";
import "./About.css";
import prof from "./assets/prof.png"

var today = new Date()
var curHr = today.getHours()

function greeting()
{
	if (curHr < 12) {
	  return <h1 className = "h1">Good Morning! I'm <name className = "name"> Elton</name>. </h1>;
	} else if (curHr < 18) {
	  return <h1 className = "h1">Good Afternoon! I'm <name className = "name"> Elton</name>. </h1>;
	} else {
	  return <h1 className = "h1">Good Evening! I'm <name className = "name"> Elton</name>. </h1>;
	}
}

class About extends Component{
	render() {
		return (
			<div>
				<div>
					<div className = "welcome_text">
						<div>
							<h1> {greeting()}</h1>
							<h1 className = "h1Sub"> I stand on the intersection where <art className = "art">art</art> and <technology className = "tech">technology</technology> meet. </h1>
							<p className = "bio"> Right now I'm a junior studying computer science at UC Irvine and pursuing a minor in digital art. I love art and music and will always appreciate a good puzzle. Whenever I'm not busy working on schoolwork or personal projects, I like to play the piano or just doodle away mindlessly. As a computer science major and an avid drawing enthusiast, I stand on the intersection of where coding and art meet, and seek to combine these two opposite ends of the spectrum. Feel free to look at what Iâ€™ve done!</p>
						</div>
						<img className = "profile_pic"
						 src = {prof}
						/>
					</div>
					<a href="link here" className="resume">My Resume</a>
				</div>
				<hr className = "hr"></hr>
			</div>
		);
	}
}

export default About;
