import React, {Component} from "react";
import "./Art.css";
import aos from 'aos';
import 'aos/dist/aos.css';

import egg from './assets/art/egg.jpg'
import elephant from './assets/art/elephant.jpg'
import island from './assets/art/island.jpg'
import jam from './assets/art/jam.jpg'
import monolith from './assets/art/monolith.jpg'
import owl from './assets/art/owl.jpg'
import turtle from './assets/art/turtle.jpg'
import whale from './assets/art/whale.jpg'

aos.init();

class Art extends Component{
	componentDidMount(){
		var modal = document.getElementsByClassName("myModal");

		// Get the button that opens the modal
		var btn = document.getElementsByClassName("myBtn");

		// Get the <span> element that closes the modal
		var span = document.getElementsByClassName("close");

		// When the user clicks the button, open the modal 
		btn[0].onclick = function() {
		  modal[0].style.display = "block";
		}

		// When the user clicks on <span> (x), close the modal
		span[0].onclick = function() {
		  modal[0].style.display = "none";
		}

		// When the user clicks anywhere outside of the modal, close it
		window.onclick = function(event) {
		  if (event.target == modal[0]) {
		    modal[0].style.display = "none";
		  }
		}
	}
	render() {
		return (
			<div 
			data-aos="slide-right"
				data-aos-delay="200"
				data-aos-duration="700"
				data-aos-once="true"
				id = "Art">
				<h1 className = "artHeader"><my className = "my">MY</my> ART</h1>
				<div className = "artLineOdd">
					<img className = "artImg" src = {turtle}/>
					<div className="myModal modal">
						  <div className="modal-content">
						    <span className="close">&times;</span>
						    <div className = "projectSplit">
						    	<div className = "text">
							    	<h1 className = "textTitle">Title</h1>
							    </div>
						    </div>
						  </div>
						</div>
					<img className = "artImg" src = {whale}/>
					<img className = "artImg" src = {jam}/>
				</div>

				<div className = "artLineEven">
					<img className = "artImg" src = {elephant}/>
					<img className = "artImg" src = {egg}/>
				</div>

				<div className = "artLineOdd">
					<img className = "artImg" src = {island}/>
					<img className = "artImg" src = {monolith}/>
					<img className = "artImg" src = {owl}/>
				</div>
			</div>
		);
	}
}

export default Art;
